mapper.select:
SELECT OP_CODE "OPCODE"
FROM GROUPTEAMVIEW
WHERE MEM_CODE = #{memCode }

SELECT LEA_PC_CODE "LEAPCCODE"
FROM LEAVE
WHERE LEA_PC_CODE = #{leavePcCode}

mapper.update:
	DECLARE
        VV_EVA_NO   EVALUATION.EVA_NO%TYPE;
        VV_ER_NO    EVALUATION_RESULT.ER_NO%TYPE;
    BEGIN
        VV_EVA_NO := EVALUATION_SEQ.NEXTVAL;
        VV_ER_NO := EVALUATION_RESULT_SEQ.NEXTVAL; -- 시퀀스 값을 변수에 저장
        
        INSERT INTO EVALUATION(EVA_NO, EVA_DATE, EVA_QUE_NO, EVA_PC_CODE)
        VALUES(VV_EVA_NO, SYSDATE, 1, 'PC_14');
            
        INSERT ALL
            INTO EVALUATION_RESULT(ER_NO, ER_EVA_NO, ER_PC_CODE) 
            VALUES (VV_ER_NO, VV_EVA_NO, 'PC_15') 
            INTO EVALUATION_RESULT(ER_NO, ER_EVA_NO, ER_PC_CODE) 
            VALUES (VV_ER_NO+1, VV_EVA_NO, 'PC_16') 
        SELECT * FROM DUAL;
        
    END;
